def getConfig(name):
    '''
    Module function to get a configuration value.

    @param name name of the configuration value (string)
    '''
    try:
        return _pkg_config[name]
    except KeyError:
        pass

    raise AttributeError('"{0}" is not a valid configuration value'.format(name))
